import React, { ChangeEvent, FC, useState } from "react";
import "./App.css";

const localStorKey = "tasks";

const addItemsToLocalStor = (array: string[]) => {
  localStorage.setItem(localStorKey, JSON.stringify(array));
};

const getItemsFromLocalStor = () => {
  const storedItems = localStorage.getItem(localStorKey);
  return storedItems ? JSON.parse(storedItems) : [];
};

const App: FC = () => {
  const initialTasks = getItemsFromLocalStor();
  const [tasks, setTasks] = useState<string[]>(initialTasks);
  const [task, setTask] = useState<string>("");
  const [filteredTasks, setFilteredTasks] = useState<string[]>(initialTasks);
  const [isEditTask, setIsEditTask] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [editableTask, setEditableTask] = useState<string>("");

  const handleDeleteTask = (indexTask: number) => {
    const newTasksList = tasks.filter((_, index) => index !== indexTask);
    setTasks(newTasksList);
    setFilteredTasks(newTasksList);
    addItemsToLocalStor(newTasksList);
  };

  const handleEditTask = (index: number) => {
    setEditIndex(index);
    setEditableTask(tasks[index]);
    setIsEditTask(true);
  };

  const handleCloseEditTask = () => {
    setIsEditTask(false);
    setEditIndex(null);
  };

  const handleSaveEditTask = () => {
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, index) =>
        index === editIndex ? editableTask : task
      );
      setTasks(updatedTasks);
      setFilteredTasks(updatedTasks);
      addItemsToLocalStor(updatedTasks);
      handleCloseEditTask();
    }
  };

  const handleAddTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (task.trim() !== "" && !tasks.includes(task.trim())) {
      const newTasksList = [...tasks, task.trim()];
      setTasks(newTasksList);
      setFilteredTasks(newTasksList);
      addItemsToLocalStor(newTasksList);
      setTask("");
    }
  };

  const handleSortByAZ = () => {
    const newTasksList = [...tasks].sort((a, b) => a.localeCompare(b));
    setTasks(newTasksList);
    setFilteredTasks(newTasksList);
    addItemsToLocalStor(newTasksList);
  };

  const handleSortByZA = () => {
    const newTasksList = [...tasks].sort((a, b) => b.localeCompare(a));
    setTasks(newTasksList);
    setFilteredTasks(newTasksList);
    addItemsToLocalStor(newTasksList);
  };

  const handleUpdateTask = (e: ChangeEvent<HTMLInputElement>) => {
    setEditableTask(e.target.value);
  };

  const handleFilteredByText = (e: ChangeEvent<HTMLInputElement>) => {
    const filterText = e.target.value.toLowerCase();
    const filteredByText = tasks.filter((item) =>
      item.toLowerCase().includes(filterText)
    );
    setFilteredTasks(filteredByText);
  };

  return (
    <div>
      <div>
        {filteredTasks.map((task, index) =>
          !isEditTask || editIndex !== index ? (
            <div key={index} className="task-item">
              <p>{task}</p>
              <button onClick={() => handleDeleteTask(index)}>Delete</button>
              <button onClick={() => handleEditTask(index)}>Edit</button>
            </div>
          ) : (
            <div key={index} className="task-edit">
              <input
                type="text"
                placeholder="Edit task"
                value={editableTask}
                onChange={handleUpdateTask}
              />
              <button onClick={handleSaveEditTask}>Save</button>
              <button onClick={handleCloseEditTask}>Cancel</button>
            </div>
          )
        )}

        <div>
          <form onSubmit={handleAddTask}>
            <input
              type="text"
              placeholder="New task"
              value={task}
              onChange={(e: ChangeEvent<HTMLInputElement>) =>
                setTask(e.target.value)
              }
            />
            <button type="submit">Add Task</button>
          </form>
          <button onClick={handleSortByAZ}>Sort A-Z</button>
          <button onClick={handleSortByZA}>Sort Z-A</button>
          <input onChange={handleFilteredByText} placeholder="Filter tasks" />
        </div>
      </div>
    </div>
  );
};

export default App;
