import React, { ChangeEvent, FC, useState } from "react";
import "./App.css";

// Начальный список задач
const configList = ["Task 1", "Task 2", "Task 3"];

// Основной компонент приложения
const App: FC = () => {
  // Состояние для списка задач
  const [tasks, setTasks] = useState<string[]>([...configList]);
  // Состояние для новой задачи
  const [task, setTask] = useState<string>("");
  // Состояние для режима редактирования и индекса редактируемой задачи
  const [isEditTask, setIsEditTask] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  // Состояние для редактируемого значения задачи
  const [editableTask, setEditableTask] = useState<string>("");

  // Функция для удаления задачи по индексу
  const handleDeleteTask = (indexTask: number) => {
    const newTasksList = tasks.filter((_, index) => index !== indexTask);
    setTasks(newTasksList);
  };

  // Функция для обработки изменения новой задачи
  const handleChangeTask = (e: ChangeEvent<HTMLInputElement>) => {
    setTask(e.target.value);
  };

  // Функция для начала редактирования задачи
  const handleStartEditTask = (index: number) => {
    setIsEditTask(true);
    setEditIndex(index);
    setEditableTask(tasks[index]);
  };

  // Функция для отмены редактирования
  const handleCancelEditTask = () => {
    setIsEditTask(false);
    setEditIndex(null);
  };

  // Функция для изменения редактируемой задачи
  const handleChangeEditTask = (e: ChangeEvent<HTMLInputElement>) => {
    setEditableTask(e.target.value);
  };

  // Функция для сохранения измененной задачи
  const handleSaveEditTask = () => {
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, index) =>
        index === editIndex ? editableTask : task
      );
      setTasks(updatedTasks);
      setIsEditTask(false);
      setEditIndex(null);
    }
  };

  // Функция для добавления новой задачи
  const handleAddTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (task.trim() !== "") {
      setTasks([...tasks, task]);
      setTask("");
    }
  };

  return (
    <div>
      {tasks.map((task, index) =>
        !isEditTask || index !== editIndex ? (
          <div key={index}>
            <p>{task}</p>
            <button onClick={() => handleDeleteTask(index)}>
              Удалить задачу
            </button>
            <button onClick={() => handleStartEditTask(index)}>
              Редактировать
            </button>
          </div>
        ) : (
          <div key={index}>
            <input
              type="text"
              placeholder="Редактировать задачу"
              value={editableTask}
              onChange={handleChangeEditTask}
            />
            <button onClick={handleSaveEditTask}>Сохранить</button>
            <button onClick={handleCancelEditTask}>Отменить</button>
          </div>
        )
      )}

      <div>
        <form onSubmit={handleAddTask}>
          <input
            type="text"
            placeholder="Введите задачу"
            value={task}
            onChange={handleChangeTask}
          />
          <button type="submit">Добавить задачу</button>
        </form>
      </div>
    </div>
  );
};

export default App;
