import React, { ChangeEvent, FC, useState } from "react";
import "./App.css";

const configList = ["Task 1", "Task 2", "Task 3"];

const App: FC = () => {
  const [tasks, setTasks] = useState<string[]>([...configList]);
  const [task, setTask] = useState<string>("");
  const [isEditTask, setIsEditTask] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [editableTask, setEditableTask] = useState<string>("");

  console.log(tasks);

  const handleDeleteTask = (indexTask: number) => {
    const newTasksList = tasks.filter((_, index) => index !== indexTask);
    setTasks(newTasksList);
  };

  const handleChangeEditTask = (e: ChangeEvent<HTMLInputElement>) => {
    setTask(e.target.value);
  };

  const handleEditTask = (index: number) => {
    setEditIndex(index);
    setEditableTask(tasks[index]);
    setIsEditTask(true);
  };

  const handleCloseEditTask = () => {
    setIsEditTask(false);
    setEditIndex(null);
  };

  const handleSaveEditTask = () => {
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, index) =>
        index === editIndex ? editableTask : task
      );
      setTasks(updatedTasks);
      setIsEditTask(false);
      setEditIndex(null);
    }
  };

  const handleAddTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (task.trim() !== "") {
      const newTasksList = [...tasks, task];
      setTasks(newTasksList);
      setTask("");
    }
  };

  const handleUpdateTask = (e: ChangeEvent<HTMLInputElement>) => {
    setEditableTask(e.target.value); // Corrected type from HTMLSelectElement to HTMLInputElement
  };

  return (
    <div>
      {tasks.map((task, index) =>
        !isEditTask || editIndex !== index ? ( // Fixed the conditional to ensure only one task is editable at a time
          <div key={index}>
            <p>{task}</p>
            <button onClick={() => handleDeleteTask(index)}>Delete task</button>
            <button onClick={() => handleEditTask(index)}>Edit</button>
          </div>
        ) : (
          <div key={index}>
            <input
              type="text"
              placeholder="Input task"
              value={editableTask}
              onChange={handleUpdateTask}
            />
            <button onClick={handleSaveEditTask}>Save</button>{" "}
            {/* Updated button text from "Close Edit" to "Save" */}
            <button onClick={handleCloseEditTask}>Cancel</button>{" "}
            {/* Updated button text from "Close Edit" to "Cancel" */}
          </div>
        )
      )}

      <div>
        <form onSubmit={handleAddTask}>
          <input
            type="text"
            placeholder="Input task"
            value={task}
            onChange={handleChangeEditTask}
          />
          <button type="submit">Add Task</button>{" "}
        </form>
      </div>
    </div>
  );
};

export default App;
