import React, { ChangeEvent, FC, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

const configList = ["Task 1", "Task 2", "Task 3"];

const App: FC = () => {
  const [tasks, setTasks] = useState<string[]>([...configList]);
  const [task, setTask] = useState<string>("");
  const [isEditTask, setIsEditTask] = useState<boolean>(false);
  const [editableTask, setEditableTask] = useState<string>("");

  const handleDeleteTask = (indexTask: number) => {
    const newTasksList = tasks.filter((item, index) => index !== indexTask);

    setTasks(newTasksList);
  };

  console.log(tasks);

  const handleChangeTask = (e: ChangeEvent<HTMLInputElement>) => {
    setTask(e.target.value);
  };

  const handleChangeEditTask = () => {
    setIsEditTask(true);
  };
  const handleChangeCloseEditTask = () => {
    setIsEditTask(false);
  };

  const handleAddTask = (e: React.FormEvent) => {
    e.preventDefault();

    if (task.trim() !== "") {
      const newTasksList = [...tasks, task];
      setTasks(newTasksList);
      setTask("");
    }
  };

  const handleEditTask = () => {};

  return (
    <div>
      {tasks.map((task, index) => (
        <div key={index}>
          <p>{task}</p>
          <button onClick={() => handleDeleteTask(index)}>Delite task</button>
        </div>
      ))}

      <div>
        <form onSubmit={handleAddTask}>
          {!isEditTask ? (
            <div>
              <input
                type="text"
                placeholder="Input task"
                value={task}
                onChange={handleChangeTask}
              />
              <button onClick={handleChangeEditTask}>Edit</button>
            </div>
          ) : (
            <button onClick={handleChangeCloseEditTask}>Edit</button>
          )}

          <button type="submit">add task</button>
        </form>
      </div>
    </div>
  );
};

export default App;
