import React, { ChangeEvent, FC, useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

const configList = ["Task 1", "Task 2", "Task 3"];

const App: FC = () => {
  const [tasks, setTasks] = useState<string[]>([...configList]);
  const [task, setTask] = useState<string>("");
  const [isEditTask, setIsEditTask] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [editableTask, setEditableTask] = useState<string>("");

  const handleDeleteTask = (indexTask: number) => {
    const newTasksList = tasks.filter((item, index) => index !== indexTask);

    setTasks(newTasksList);
  };

  console.log(tasks);

  const handleChangeEditTask = (e: ChangeEvent<HTMLInputElement>) => {
    setTask(e.target.value);
  };

  const handleEditTask = (index: number) => {
    setEditIndex(index);
    setEditableTask(tasks[index]);
    setIsEditTask(true);
  };
  const handleCloseEditTask = () => {
    setIsEditTask(false);
    setEditIndex(null);
  };

  const handleSaveEditTask = () => {
    if (editIndex !== null) {
      const updatedTasks = tasks.map((task, index) =>
        index === editIndex ? editableTask : task
      );
      setTasks(updatedTasks);
      setIsEditTask(false);
      setEditIndex(null);
    }
  };

  // useEffect(() => {
  //   setEditableTask(task);
  // }, []);

  const handleAddTask = (e: React.FormEvent) => {
    e.preventDefault();

    if (task.trim() !== "") {
      const newTasksList = [...tasks, task];
      setTasks(newTasksList);
      setTask("");
    }
  };

  const handleUpdateTask = (e: ChangeEvent<HTMLSelectElement>) => {
    setEditableTask(e.target.value);
  };

  return (
    <div>
      {tasks.map((task, index) =>
        !isEditTask ? (
          <div key={index}>
            <p>{task}</p>
            <button onClick={() => handleDeleteTask(index)}>Delete task</button>
            <button onClick={() => handleChangeEditTask(index)}>Edit</button>
          </div>
        ) : (
          <div key={index}>
            <input
              type="text"
              placeholder="Input task"
              value={editableTask}
              onChange={() => handleUpdateTask}
            />
            <button onClick={handleSaveEditTask}>Close Edit</button>
            <button onClick={handleChangeCloseEditTask}>Close Edit</button>
          </div>
        )
      )}

      <div>
        <form onSubmit={handleAddTask}>
          <input
            type="text"
            placeholder="Input task"
            value={task}
            onChange={handleChangeTask}
          />

          <button type="submit">add task</button>
        </form>
      </div>
    </div>
  );
};

export default App;
